mapper.select:

 		select * 
 		from(select @rownum := @rownum + 1 as rn, id, pw, mname, mnick, mregdate, mbirth, mtype, mpoint
 			from fb_member, (select @rownum := 0) as rowcolumn 
 			where id is not null



			) as rownum_table where rn <= #{pageIndex} * #{countPerPage}
 			and rn > (#{pageIndex}-1) * #{countPerPage}
